import { apiFetch } from "./client";

export const fetchFacilities = () => apiFetch("/api/facilities");

export const createFacility = (payload) => apiFetch("/api/facilities", { 
  method: "POST", body: JSON.stringify(payload) 
});

export const updateFacility = (id, payload) => apiFetch(`/api/facilities/${id}`, { 
  method: "PUT", body: JSON.stringify(payload) 
});

export const deactivateFacility = (id) => apiFetch(`/api/facilities/${id}/deactivate`, { 
  method: "PATCH" 
});

export const fetchMyFacilityBookings = () => apiFetch("/api/facilities/my-bookings");

export const fetchFacilityBookings = () => apiFetch("/api/facilities/bookings");

export const bookFacility = (id, payload) => apiFetch(`/api/facilities/${id}/book`, { 
  method: "POST", body: JSON.stringify(payload) 
});

export const updateFacilityBookingStatus = (id, status) => apiFetch(`/api/facilities/bookings/${id}`, { 
  method: "PATCH", body: JSON.stringify({ status }) 
});

export const cancelFacilityBooking = (id) => apiFetch(`/api/facilities/bookings/${id}/cancel`, { 
  method: "PATCH" 
});

export const payForFacilityBooking = (id, method) => apiFetch(`/api/facilities/bookings/${id}/pay`, { 
  method: "POST", body: JSON.stringify({ payment_method: method }) 
});

export const requestFacilityRefund = (paymentId, reason) => apiFetch("/api/refunds/facility/request", { 
  method: "POST", body: JSON.stringify({ payment_id: paymentId, reason }) 
});

export const fetchFacilityBookingsByDate = (id, date) => apiFetch(`/api/facilities/${id}/bookings?date=${date}`);
