import { useState } from "react";
import { useNavigate, Link } from "react-router-dom"; // ✅ Added Link
import { signupUser } from "../../api/auth.api";

export default function Signup() {
  const navigate = useNavigate();

  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("RESIDENT");
  const [loading, setLoading] = useState(false);

  async function handleSignup(e) {
    e.preventDefault();

    try {
      setLoading(true);
      await signupUser({
        full_name: fullName,
        email,
        password,
        role,
      });

      alert("Signup successful ✅ Now login.");
      navigate("/login");
    } catch (err) {
      alert(err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={{ padding: 40, maxWidth: 450, margin: "0 auto" }}>
      <h2>Create Account</h2>

      <form onSubmit={handleSignup} style={{ display: "grid", gap: 12 }}>
        <input
          style={inputStyle}
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          required
        />

        <input
          style={inputStyle}
          placeholder="Email"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <input
          style={inputStyle}
          placeholder="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <div style={{ display: "flex", flexDirection: "column", gap: 4 }}>
          <label style={{ fontSize: "0.85rem", color: "#666" }}>Select Role:</label>
          <select 
            style={inputStyle} 
            value={role} 
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="RESIDENT">Resident</option>
            <option value="SELLER">Seller (Marketplace)</option>
            <option value="SECURITY">Security</option>
            <option value="WORKER">Worker</option>
          </select>
        </div>

        <button 
          type="submit" 
          disabled={loading}
          style={buttonStyle}
        >
          {loading ? "Creating..." : "Sign Up"}
        </button>

        <p style={{ textAlign: "center", marginTop: 10, fontSize: "0.9rem" }}>
          Already have an account? <Link to="/login">Login here</Link>
        </p>
      </form>
    </div>
  );
}

// Basic styles to match the login feel
const inputStyle = {
  padding: "10px",
  borderRadius: "6px",
  border: "1px solid #ddd",
};

const buttonStyle = {
  padding: "12px",
  background: "#007bff",
  color: "#fff",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  fontWeight: "bold",
};
