import { useEffect, useMemo, useState, useCallback } from "react";
import { useAuth } from "../../auth/AuthContext";
import {
  fetchMarketplaceProducts,
  createMarketplaceProduct,
  placeMarketplaceOrder,
  fetchMarketplaceOrders,
  approveMarketplaceProduct,
} from "../../api/marketplace.api";

export default function MarketplacePage() {
  const { profile, loading: authLoading } = useAuth();

  const roleName =
    profile?.roles?.name || profile?.role || profile?.user_roles?.role || null;

  const isAdmin = roleName === "ADMIN";
  const isResident = roleName === "RESIDENT";

  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  const [loading, setLoading] = useState(true);

  // Post product form
  const [pName, setPName] = useState("");
  const [pDesc, setPDesc] = useState("");
  const [pCategory, setPCategory] = useState("GENERAL");
  const [pPrice, setPPrice] = useState("");
  const [pQty, setPQty] = useState("");

  const [posting, setPosting] = useState(false);

  // Ordering
  const [orderQtyMap, setOrderQtyMap] = useState({});
  const [orderingId, setOrderingId] = useState(null);

  const categories = useMemo(
    () => ["GENERAL", "FOOD", "GROCERY", "ELECTRONICS", "HOME", "OTHER"],
    []
  );

  const loadData = useCallback(async () => {
    try {
      setLoading(true);
      const [prodData, orderData] = await Promise.all([
        fetchMarketplaceProducts(),
        fetchMarketplaceOrders(),
      ]);

      setProducts(prodData || []);
      setOrders(orderData || []);
    } catch (err) {
      console.log("❌ Marketplace load failed:", err.message);
      alert(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (!authLoading && profile) {
      loadData();
    }
  }, [authLoading, profile, loadData]);

  async function handlePostProduct(e) {
    e.preventDefault();

    if (!pName || !pPrice || !pQty) {
      alert("Name, price and quantity are required");
      return;
    }

    try {
      setPosting(true);

      await createMarketplaceProduct({
        name: pName,
        description: pDesc,
        category: pCategory,
        price: Number(pPrice),
        quantity: Number(pQty),
      });

      alert("Product submitted ✅ (waiting for admin approval)");
      setPName("");
      setPDesc("");
      setPCategory("GENERAL");
      setPPrice("");
      setPQty("");

      await loadData();
    } catch (err) {
      alert(err.message);
    } finally {
      setPosting(false);
    }
  }

  async function handlePlaceOrder(productId) {
    const qty = Number(orderQtyMap[productId] || 1);

    if (!qty || qty <= 0) {
      alert("Enter a valid quantity");
      return;
    }

    try {
      setOrderingId(productId);

      await placeMarketplaceOrder({
        product_id: productId,
        quantity: qty,
      });

      alert("Order placed ✅");
      await loadData();
    } catch (err) {
      alert(err.message);
    } finally {
      setOrderingId(null);
    }
  }

  async function handleApprove(productId) {
    if (!window.confirm("Approve this product?")) return;

    try {
      await approveMarketplaceProduct(productId);
      alert("Approved ✅");
      await loadData();
    } catch (err) {
      alert(err.message);
    }
  }

  if (authLoading || !profile) {
    return (
      <div style={{ padding: 40, textAlign: "center" }}>
        <h3>Verifying session...</h3>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Marketplace</h2>
        <button onClick={loadData}>Refresh</button>
      </div>

      <div style={{ marginBottom: 16 }}>
        <strong>Role:</strong> {roleName || "Unknown"}
      </div>

      {/* POST PRODUCT (Seller only, backend validates seller approval) */}
      <div style={cardStyle}>
        <h3>Post a Product (Seller)</h3>
        <p style={{ color: "#666", marginTop: 0 }}>
          Only approved sellers can post products.
        </p>

        <form onSubmit={handlePostProduct} style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
          <input
            style={inputStyle}
            placeholder="Product name"
            value={pName}
            onChange={(e) => setPName(e.target.value)}
          />

          <input
            style={inputStyle}
            placeholder="Description"
            value={pDesc}
            onChange={(e) => setPDesc(e.target.value)}
          />

          <select
            style={inputStyle}
            value={pCategory}
            onChange={(e) => setPCategory(e.target.value)}
          >
            {categories.map((c) => (
              <option key={c} value={c}>
                {c}
              </option>
            ))}
          </select>

          <input
            style={inputStyle}
            type="number"
            placeholder="Price"
            value={pPrice}
            onChange={(e) => setPPrice(e.target.value)}
          />

          <input
            style={inputStyle}
            type="number"
            placeholder="Quantity"
            value={pQty}
            onChange={(e) => setPQty(e.target.value)}
          />

          <button type="submit" disabled={posting}>
            {posting ? "Posting..." : "Post Product"}
          </button>
        </form>
      </div>

      {/* PRODUCTS LIST */}
      <div style={cardStyle}>
        <h3>Available Products</h3>

        {loading ? (
          <p>Loading products...</p>
        ) : products.length === 0 ? (
          <p>No approved products available.</p>
        ) : (
          <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(260px, 1fr))", gap: 12 }}>
            {products.map((p) => (
              <div key={p.id} style={productCardStyle}>
                <h4 style={{ margin: 0 }}>{p.name}</h4>
                <p style={{ margin: "6px 0", color: "#666" }}>
                  {p.description || "No description"}
                </p>
                <p style={{ margin: "6px 0" }}>
                  <b>Category:</b> {p.category || "GENERAL"}
                </p>
                <p style={{ margin: "6px 0" }}>
                  <b>Price:</b> ₹{p.price}
                </p>
                <p style={{ margin: "6px 0" }}>
                  <b>Stock:</b> {p.quantity}
                </p>

                {isResident && (
                  <div style={{ display: "flex", gap: 8, marginTop: 10 }}>
                    <input
                      type="number"
                      min="1"
                      style={{ width: 80 }}
                      value={orderQtyMap[p.id] || 1}
                      onChange={(e) =>
                        setOrderQtyMap((prev) => ({ ...prev, [p.id]: e.target.value }))
                      }
                    />
                    <button
                      onClick={() => handlePlaceOrder(p.id)}
                      disabled={orderingId === p.id}
                    >
                      {orderingId === p.id ? "Ordering..." : "Request / Buy"}
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* ORDERS */}
      <div style={cardStyle}>
        <h3>Orders</h3>

        {loading ? (
          <p>Loading orders...</p>
        ) : orders.length === 0 ? (
          <p>No orders found.</p>
        ) : (
          <table width="100%" cellPadding="10" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr style={{ background: "#f3f3f3" }}>
                <th align="left">Product</th>
                <th align="left">Qty</th>
                <th align="left">Status</th>
                <th align="left">Created</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((o) => (
                <tr key={o.id} style={{ borderBottom: "1px solid #eee" }}>
                  <td>{o.marketplace_products?.name || o.product_id}</td>
                  <td>{o.quantity}</td>
                  <td><b>{o.status}</b></td>
                  <td>{o.created_at ? new Date(o.created_at).toLocaleString() : "-"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* OPTIONAL ADMIN APPROVAL SECTION (only if you later fetch unapproved products) */}
      {isAdmin && (
        <div style={cardStyle}>
          <h3>Admin Note</h3>
          <p style={{ color: "#666" }}>
            Admin approval endpoint exists: <code>/api/marketplace/products/:id/approve</code>.
            <br />
            Currently UI shows only approved products, so approval list is not visible here.
          </p>
        </div>
      )}
    </div>
  );
}

const cardStyle = {
  background: "#fff",
  padding: 16,
  borderRadius: 10,
  marginBottom: 16,
};

const inputStyle = {
  padding: "10px",
  borderRadius: 6,
  border: "1px solid #ddd",
};

const productCardStyle = {
  background: "#fafafa",
  padding: 14,
  borderRadius: 10,
  border: "1px solid #eee",
};
