import { createContext, useContext, useEffect, useState } from "react";
import { supabase } from "../lib/supabase";

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  // Helper: Load Profile safely
  async function loadProfile(userId) {
    try {
      const { data, error } = await supabase
        .from("profiles")
        .select("*, roles(name)")
        .eq("id", userId)
        .single();

      if (error) {
        // Only log if it's not a specific "row not found" which might be expected for new users
        console.warn("Profile load warning:", error.message);
      }
      return data;
    } catch (err) {
      console.error("Profile fetch crashed:", err);
      return null;
    }
  }

  useEffect(() => {
    let mounted = true;

    // ðŸš¨ SAFETY VALVE: If Supabase gets stuck, force the app to load after 2 seconds
    const safetyTimer = setTimeout(() => {
      if (mounted && loading) {
        console.warn("âš ï¸ Auth check timed out. Forcing app to load.");
        setLoading(false);
      }
    }, 2000);

    async function initializeAuth() {
      try {
        // 1. Get current session
        const { data: { session } } = await supabase.auth.getSession();

        if (session?.user) {
          setUser(session.user);
          // 2. Load profile
          const p = await loadProfile(session.user.id);
          if (mounted) setProfile(p);
        }
      } catch (error) {
        console.error("Auth initialization failed:", error);
      } finally {
        // 3. ALWAYS finish loading
        if (mounted) {
          setLoading(false);
          clearTimeout(safetyTimer); // Clear the safety timer since we finished successfully
        }
      }
    }

    initializeAuth();

    // 4. Listen for auth changes (Login/Logout)
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        if (!mounted) return;

        const currentUser = session?.user ?? null;
        setUser(currentUser);

        if (event === 'SIGNED_OUT') {
          setProfile(null);
          setLoading(false);
        } else if (currentUser && !profile) {
          // If we have a user but no profile yet, fetch it
          const p = await loadProfile(currentUser.id);
          if (mounted) setProfile(p);
        }
      }
    );

    return () => {
      mounted = false;
      clearTimeout(safetyTimer);
      subscription.unsubscribe();
    };
    // ðŸ›‘ This ignores the warning because we only want this to run ONCE on mount
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const logout = async () => {
    setLoading(true);
    await supabase.auth.signOut();
    setUser(null);
    setProfile(null);
    setLoading(false);
  };

  return (
    <AuthContext.Provider value={{ user, profile, loading, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
