import { useEffect, useMemo, useState, useCallback } from "react";
import { useAuth } from "../../auth/AuthContext";
import {
  fetchMarketplaceProducts,
  fetchAllMarketplaceProducts,
  approveMarketplaceProduct,
  createMarketplaceProduct,
  placeMarketplaceOrder, // Corrected name to match marketplace.api.js
  fetchMarketplaceOrders, // Ensure this is imported
} from "../../api/marketplace.api";

export default function MarketplacePage() {
  const { profile, loading: authLoading } = useAuth();

  const roleName =
    profile?.roles?.name || profile?.role || profile?.user_roles?.role || null;

  const isAdmin = roleName === "ADMIN";
  const isResident = roleName === "RESIDENT";

  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Post product form states
  const [pName, setPName] = useState("");
  const [pDesc, setPDesc] = useState("");
  const [pCategory, setPCategory] = useState("GENERAL");
  const [pPrice, setPPrice] = useState("");
  const [pQty, setPQty] = useState("");
  const [posting, setPosting] = useState(false);

  // Ordering states
  const [orderQtyMap, setOrderQtyMap] = useState({});
  const [orderingId, setOrderingId] = useState(null);

  const categories = useMemo(
    () => ["GENERAL", "FOOD", "GROCERY", "ELECTRONICS", "HOME", "OTHER"],
    []
  );

  /**
   * LOAD DATA (Admin sees ALL, Residents see APPROVED)
   */
  const loadData = useCallback(async () => {
    try {
      setLoading(true);

      // Determine which product fetcher to use
      const productPromise = isAdmin 
        ? fetchAllMarketplaceProducts() 
        : fetchMarketplaceProducts();

      const [prodData, orderData] = await Promise.all([
        productPromise,
        fetchMarketplaceOrders(),
      ]);

      setProducts(prodData || []);
      setOrders(orderData || []);
    } catch (err) {
      console.log("❌ Marketplace load failed:", err.message);
      alert(err.message);
    } finally {
      setLoading(false);
    }
  }, [isAdmin]);

  useEffect(() => {
    if (!authLoading && profile) {
      loadData();
    }
  }, [authLoading, profile, loadData]);

  async function handlePostProduct(e) {
    e.preventDefault();
    if (!pName || !pPrice || !pQty) {
      alert("Name, price and quantity are required");
      return;
    }

    try {
      setPosting(true);
      await createMarketplaceProduct({
        name: pName,
        description: pDesc,
        category: pCategory,
        price: Number(pPrice),
        quantity: Number(pQty),
      });

      alert("Product submitted ✅ (waiting for admin approval)");
      setPName("");
      setPDesc("");
      setPCategory("GENERAL");
      setPPrice("");
      setPQty("");

      await loadData();
    } catch (err) {
      alert(err.message);
    } finally {
      setPosting(false);
    }
  }

  async function handlePlaceOrder(productId) {
    const qty = Number(orderQtyMap[productId] || 1);
    if (!qty || qty <= 0) {
      alert("Enter a valid quantity");
      return;
    }

    try {
      setOrderingId(productId);
      await placeMarketplaceOrder({
        product_id: productId,
        quantity: qty,
      });

      alert("Order placed ✅");
      await loadData();
    } catch (err) {
      alert(err.message);
    } finally {
      setOrderingId(null);
    }
  }

  async function handleApprove(productId) {
    if (!window.confirm("Approve this product?")) return;
    try {
      await approveMarketplaceProduct(productId);
      alert("Approved ✅");
      await loadData();
    } catch (err) {
      alert(err.message);
    }
  }

  if (authLoading || !profile) {
    return (
      <div style={{ padding: 40, textAlign: "center" }}>
        <h3>Verifying session...</h3>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Marketplace</h2>
        <button onClick={loadData}>Refresh</button>
      </div>

      <div style={{ marginBottom: 16 }}>
        <strong>Role:</strong> {roleName || "Unknown"}
      </div>

      {/* POST PRODUCT SECTION */}
      <div style={cardStyle}>
        <h3>Post a Product (Seller)</h3>
        <p style={{ color: "#666", marginTop: 0 }}>
          Submit your item for admin review.
        </p>

        <form onSubmit={handlePostProduct} style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
          <input
            style={inputStyle}
            placeholder="Product name"
            value={pName}
            onChange={(e) => setPName(e.target.value)}
          />
          <input
            style={inputStyle}
            placeholder="Description"
            value={pDesc}
            onChange={(e) => setPDesc(e.target.value)}
          />
          <select
            style={inputStyle}
            value={pCategory}
            onChange={(e) => setPCategory(e.target.value)}
          >
            {categories.map((c) => (
              <option key={c} value={c}>{c}</option>
            ))}
          </select>
          <input
            style={inputStyle}
            type="number"
            placeholder="Price"
            value={pPrice}
            onChange={(e) => setPPrice(e.target.value)}
          />
          <input
            style={inputStyle}
            type="number"
            placeholder="Quantity"
            value={pQty}
            onChange={(e) => setPQty(e.target.value)}
          />
          <button type="submit" disabled={posting}>
            {posting ? "Posting..." : "Post Product"}
          </button>
        </form>
      </div>

      {/* PRODUCTS LIST */}
      <div style={cardStyle}>
        <h3>Products</h3>
        {loading ? (
          <p>Loading products...</p>
        ) : products.length === 0 ? (
          <p>No products available.</p>
        ) : (
          <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(260px, 1fr))", gap: 12 }}>
            {products.map((p) => (
              <div key={p.id} style={productCardStyle}>
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "flex-start" }}>
                  <h4 style={{ margin: 0 }}>{p.name}</h4>
                  <span style={{ 
                    fontSize: "0.7rem", 
                    padding: "2px 6px", 
                    borderRadius: 4, 
                    background: p.is_approved ? "#e6fffa" : "#fffbe6",
                    color: p.is_approved ? "#2c7a7b" : "#b7791f",
                    border: p.is_approved ? "1px solid #b2f5ea" : "1px solid #fef3c7"
                  }}>
                    {p.is_approved ? "LIVE" : "PENDING"}
                  </span>
                </div>

                <p style={{ margin: "6px 0", color: "#666", fontSize: "0.9rem" }}>
                  {p.description || "No description"}
                </p>
                <p style={{ margin: "4px 0", fontSize: "0.9rem" }}>
                  <b>Price:</b> ₹{p.price} | <b>Stock:</b> {p.quantity}
                </p>

                {/* ADMIN: Approve Button */}
                {isAdmin && !p.is_approved && (
                  <button 
                    onClick={() => handleApprove(p.id)}
                    style={{ 
                      marginTop: 10, 
                      width: "100%", 
                      background: "#4CAF50", 
                      color: "white", 
                      border: "none", 
                      padding: "8px", 
                      borderRadius: 6,
                      cursor: "pointer"
                    }}
                  >
                    Approve Now
                  </button>
                )}

                {/* RESIDENT: Buy/Request Button (Only if approved) */}
                {isResident && p.is_approved && (
                  <div style={{ display: "flex", gap: 8, marginTop: 10 }}>
                    <input
                      type="number"
                      min="1"
                      style={{ width: 60, padding: "4px" }}
                      value={orderQtyMap[p.id] || 1}
                      onChange={(e) =>
                        setOrderQtyMap((prev) => ({ ...prev, [p.id]: e.target.value }))
                      }
                    />
                    <button
                      style={{ flex: 1 }}
                      onClick={() => handlePlaceOrder(p.id)}
                      disabled={orderingId === p.id}
                    >
                      {orderingId === p.id ? "..." : "Buy"}
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* ORDERS TABLE */}
      <div style={cardStyle}>
        <h3>Your Orders</h3>
        {loading ? (
          <p>Loading orders...</p>
        ) : orders.length === 0 ? (
          <p>No orders found.</p>
        ) : (
          <div style={{ overflowX: "auto" }}>
            <table width="100%" cellPadding="10" style={{ borderCollapse: "collapse", minWidth: 500 }}>
              <thead>
                <tr style={{ background: "#f3f3f3", borderBottom: "2px solid #ddd" }}>
                  <th align="left">Product</th>
                  <th align="left">Qty</th>
                  <th align="left">Status</th>
                  <th align="left">Date</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((o) => (
                  <tr key={o.id} style={{ borderBottom: "1px solid #eee" }}>
                    <td>{o.marketplace_products?.name || "Deleted Product"}</td>
                    <td>{o.quantity}</td>
                    <td><b style={{ color: o.status === "COMPLETED" ? "green" : "#333" }}>{o.status}</b></td>
                    <td>{o.created_at ? new Date(o.created_at).toLocaleDateString() : "-"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

// Styles
const cardStyle = {
  background: "#fff",
  padding: 20,
  borderRadius: 12,
  marginBottom: 20,
  boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
};

const inputStyle = {
  padding: "10px",
  borderRadius: 6,
  border: "1px solid #ddd",
  flex: "1 1 150px",
};

const productCardStyle = {
  background: "#fff",
  padding: 16,
  borderRadius: 10,
  border: "1px solid #edf2f7",
  display: "flex",
  flexDirection: "column",
  justifyContent: "space-between"
};
