import React, { useEffect, useState } from "react";
import axios from "axios";
import { generateSlots, isSlotBooked } from "../../utils/slotHelpers";
import { supabase } from "../../lib/supabase";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

function FacilityDetails({ facilityId }) {
  const [facility, setFacility] = useState(null);
  const [selectedDate, setSelectedDate] = useState("");
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetchFacility();
  }, []);

  const fetchFacility = async () => {
    const res = await axios.get(`${BACKEND_URL}/facilities/${facilityId}`);
    setFacility(res.data);
  };

  const fetchBookings = async () => {
    const {
      data: { session }
    } = await supabase.auth.getSession();

    const res = await axios.get(
      `${BACKEND_URL}/facilities/${facilityId}/bookings?date=${selectedDate}`,
      {
        headers: {
          Authorization: `Bearer ${session.access_token}`
        }
      }
    );

    setBookings(res.data);
  };

  useEffect(() => {
    if (selectedDate) fetchBookings();
  }, [selectedDate]);

  if (!facility) return <p>Loading...</p>;

  const slots = selectedDate
    ? generateSlots(
        facility.open_time,
        facility.close_time,
        facility.slot_duration_minutes,
        selectedDate
      )
    : [];

  return (
    <div>
      <h2>{facility.name}</h2>
      <p>{facility.description}</p>

      <input
        type="date"
        value={selectedDate}
        onChange={(e) => setSelectedDate(e.target.value)}
      />

      <div style={{ marginTop: "20px" }}>
        {slots.map((slot, index) => {
          const booked = isSlotBooked(slot, bookings);

          return (
            <button
              key={index}
              disabled={booked}
              style={{
                margin: "5px",
                padding: "10px",
                backgroundColor: booked ? "#ccc" : "#4CAF50",
                color: "white"
              }}
              onClick={() => handleBooking(slot)}
            >
              {slot.start.toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit"
              })}{" "}
              -{" "}
              {slot.end.toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit"
              })}
            </button>
          );
        })}
      </div>
    </div>
  );

  async function handleBooking(slot) {
    const {
      data: { session }
    } = await supabase.auth.getSession();

    await axios.post(
      `${BACKEND_URL}/facilities/${facilityId}/book`,
      {
        start_time: slot.start.toISOString(),
        end_time: slot.end.toISOString()
      },
      {
        headers: {
          Authorization: `Bearer ${session.access_token}`
        }
      }
    );

    alert("Booking created!");
    fetchBookings();
  }
}

export default FacilityDetails;
