import { createContext, useContext, useEffect, useState } from "react";
import { supabase } from "../lib/supabase";

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [debugStatus, setDebugStatus] = useState("Initializing..."); // üîç Visual Debug State

  // Helper: Load Profile safely
  async function loadProfile(userId) {
    try {
      setDebugStatus("Fetching Profile from DB...");
      const { data, error } = await supabase
        .from("profiles")
        .select("*, roles(name)")
        .eq("id", userId)
        .single();

      if (error) {
        setDebugStatus(`Profile Error: ${error.message}`);
        console.warn("Profile load warning:", error.message);
      }
      return data;
    } catch (err) {
      setDebugStatus(`Profile Crash: ${err.message}`);
      return null;
    }
  }

  useEffect(() => {
    let mounted = true;

    // üö® SAFETY VALVE: Force load after 3 seconds
    const safetyTimer = setTimeout(() => {
      if (mounted && loading) {
        setDebugStatus("‚ö†Ô∏è TIMEOUT: Forcing App Load");
        console.warn("‚ö†Ô∏è Auth check timed out. Forcing app to load.");
        setLoading(false);
      }
    }, 3000);

    async function initializeAuth() {
      try {
        setDebugStatus("Checking Session...");
        const { data: { session } } = await supabase.auth.getSession();

        if (session?.user) {
          setDebugStatus("User Found. Loading Profile...");
          setUser(session.user);
          const p = await loadProfile(session.user.id);
          if (mounted) setProfile(p);
        } else {
          setDebugStatus("No User Session Found.");
        }
      } catch (error) {
        setDebugStatus(`Auth Init Failed: ${error.message}`);
      } finally {
        if (mounted) {
          setDebugStatus("Done. Loading = false");
          setLoading(false);
          clearTimeout(safetyTimer);
        }
      }
    }

    initializeAuth();

    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        if (!mounted) return;
        const currentUser = session?.user ?? null;
        setUser(currentUser);

        if (event === 'SIGNED_OUT') {
          setProfile(null);
          setLoading(false);
        } else if (currentUser && !profile) {
          const p = await loadProfile(currentUser.id);
          if (mounted) setProfile(p);
        }
      }
    );

    return () => {
      mounted = false;
      clearTimeout(safetyTimer);
      subscription.unsubscribe();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const logout = async () => {
    setLoading(true);
    await supabase.auth.signOut();
    setUser(null);
    setProfile(null);
    setLoading(false);
  };

  // üîç IF STUCK LOADING, SHOW DEBUG SCREEN
  if (loading) {
    return (
      <div style={{ padding: 40, fontFamily: 'monospace', background: '#f0f0f0', height: '100vh' }}>
        <h2>üõ†Ô∏è Auth Debugger</h2>
        <p><strong>Status:</strong> {debugStatus}</p>
        <p><strong>Loading:</strong> {loading.toString()}</p>
        <p><strong>User:</strong> {user ? "Yes" : "No"}</p>
        <hr />
        <p>If this screen stays for more than 3 seconds, the Safety Valve failed.</p>
        <button 
          onClick={() => { setLoading(false); }}
          style={{ padding: 10, background: 'red', color: 'white', border: 'none', cursor: 'pointer' }}
        >
          FORCE ENTER APP
        </button>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, profile, loading, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
