import { useAuth } from "../auth/AuthContext";
import { supabase } from "../lib/supabase";
import { useNavigate } from "react-router-dom";

export default function Navbar() {
  const { profile } = useAuth();
  const navigate = useNavigate();

  async function handleLogout() {
    try {
      await supabase.auth.signOut();
    } catch (err) {
      console.error("Logout error:", err.message);
    } finally {
      // Remove only supabase keys (safe)
      Object.keys(localStorage).forEach((key) => {
        if (key.startsWith("sb-")) localStorage.removeItem(key);
      });
      navigate("/login");
    }
  }

  return (
    <div style={{ 
      padding: 12, 
      background: "#fff", 
      marginBottom: 16,
      borderBottom: "1px solid #eee", // optional: added for better visual separation
    }}>
      <strong>Resident Portal</strong>

      <span style={{ float: "right", display: "flex", gap: 12, alignItems: "center" }}>
        {/* âœ… Safety patch: safe access with fallback */}
        <span>
          Welcome, <strong>{profile?.full_name || "User"}</strong>
        </span>
        <button onClick={handleLogout}>Logout</button>
      </span>
    </div>
  );
}
