import { apiFetch } from "./client";

/* ---------- PRODUCTS ---------- */

// GET /api/marketplace/products
export function fetchMarketplaceProducts() {
  return apiFetch("/api/marketplace/products");
}

// POST /api/marketplace/products
export function createMarketplaceProduct(payload) {
  return apiFetch("/api/marketplace/products", {
    method: "POST",
    body: JSON.stringify(payload),
  });
}

// PATCH /api/marketplace/products/:id
export function updateMarketplaceProduct(productId, payload) {
  return apiFetch(`/api/marketplace/products/${productId}`, {
    method: "PATCH",
    body: JSON.stringify(payload),
  });
}

// PATCH /api/marketplace/products/:id/approve (ADMIN)
export function approveMarketplaceProduct(productId) {
  return apiFetch(`/api/marketplace/products/${productId}/approve`, {
    method: "PATCH",
  });
}

/* ---------- ORDERS ---------- */

// POST /api/marketplace/orders
export function placeMarketplaceOrder(payload) {
  return apiFetch("/api/marketplace/orders", {
    method: "POST",
    body: JSON.stringify(payload),
  });
}

// GET /api/marketplace/orders
export function fetchMarketplaceOrders() {
  return apiFetch("/api/marketplace/orders");
}
